	/**
	 * @param s
	 * @return The method checks whether the text is in Uppercase
	 */
	public static boolean isUpperCase(String s) {
		for (int i = 0; i < s.length(); i++) {
			if (Character.isLowerCase(s.charAt(i)))
				return false;
		}
		return true;
	}

